import { useContext } from "react";
import { AppContext } from "../context";
import {FaRegThumbsUp} from 'react-icons/fa6';



const Meals= () => {

    const {loading, meals, select_meal, add_to_favourites}= useContext(AppContext);

    if(loading) {
        return (
            <section>
                <h4>Loading...</h4>
            </section>
        )
    }

    if(meals.length < 1) {
        return(
            <section>
                <h4>Unable to find dish that you want. Please try again.</h4>
            </section>
        )
    }
    return (
        <section className="grid grid-cols-1 gap-2 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
            {meals.map((meal) => {
                const {idMeal, strMeal: title, strMealThumb: image}= meal;
                return (
                    <article key={idMeal} className="bg-gray-100 p-2">
                        <img src={image} alt={title} className="w-full h-auto max-h-[20rem] mx-auto cursor-pointer" onClick={() => select_meal(idMeal)}/>
                        <footer className="mt-2">
                            <h5 className="text-lg font-semibold">{title}</h5>
                            <button className="text-blue-500 hover:text-blue-700" onClick={() => add_to_favourites(idMeal)}><FaRegThumbsUp/></button>
                        </footer>
                    </article>
                )    
            })}
        </section>
    )
}

export default Meals;
