import { useContext } from "react";
import { AppContext } from "../context";
import {FaRegThumbsUp} from 'react-icons/fa6';



const Meals= () => {

    const {loading, meals, select_meal, add_to_favourites}= useContext(AppContext);

    if(loading) {
        return (
            <section className="flex justify-center text-xl">
                <h2>Loading...</h2>
            </section>
        )
    }

    if(meals.length < 1) {
        return(
            <section className="flex justify-center text-xl font-bold">
                <h2>Unable to find dish that you want. Please try again.</h2>
            </section>
        )
    }
    return (
        <section className="grid grid-cols-1 gap-5 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 m-5">
            {meals.map((meal) => {
                const {idMeal, strMeal: title, strMealThumb: image}= meal;
                return (
                    <article key={idMeal} className="bg-gray-100 border-[1px] border-black rounded-lg hover:ring-offset-4 hover:ring hover:ring-gray-500">
                        <div className="bg-black overflow-hidden rounded-t-lg">
                            <img src={image} alt={title} 
                            className="w-full h-full cursor-pointer object-cover hover:opacity-60 hover:scale-125 transition-transform duration-500" 
                            onClick={() => select_meal(idMeal)}/>
                        </div>
                        <footer className="flex justify-between items-center h-[4rem] p-2">
                            <h5 className="text-2xl font-semibold">{title}</h5>
                            <button className="text-blue-500 hover:text-blue-900 text-2xl p-5" onClick={() => add_to_favourites(idMeal)}><FaRegThumbsUp/></button>
                        </footer>
                    </article>
                )    
            })}
        </section>
    )
}

export default Meals;
