import { useContext } from "react";
import { AppContext } from "../context";




function Modal() {
    const {selectedMeal, close_modal}= useContext(AppContext);

    const {strMealThumb: image, strMeal:title, strInstructions: text, strSource: sorce}= selectedMeal;

    return (
        <aside>
            <div>
                <h1>{title}</h1>
                <button onClick={close_modal}>Close</button>
            </div>
        </aside>
    )
}

export default Modal;
