import { useContext } from "react";
import { AppContext } from "../context";
import {FaRegThumbsUp} from 'react-icons/fa6';



const Meals= () => {

    const {loading, meals, select_meal}= useContext(AppContext);

    if(loading) {
        return (
            <section>
                <h4>Loading...</h4>
            </section>
        )
    }

    if(meals.length < 1) {
        return(
            <section>
                <h4>Unable to find dish that you want. Please try again.</h4>
            </section>
        )
    }
    return (
        <section className="grid grid-cols-4 grid-gap-2 max-md: grid-cols-3">
            {meals.map((meal) => {
                const {idMeal, strMeal: title, strMealThumb: image}= meal;
                return (
                    <article key={idMeal}>
                        <img src={image} className="w-[10rem] max-w-[20rem] h-[10rem] max-h-[20rem]" onClick={select_meal(idMeal)}/>
                        <footer>
                            <h5>{title}</h5>
                            <button className=""><FaRegThumbsUp/></button>
                        </footer>
                    </article>
                )    
            })}
        </section>
    )
}

export default Meals;
