import { useContext } from "react";
import { AppContext } from "../context";




function Modal() {
    const {selectedMeal, close_modal}= useContext(AppContext);

    const {strMealThumb: image, strMeal:title, strInstructions: text, strSource: source}= selectedMeal;

    return (
        <aside className="absolute bg-black w-full z-[1] lg:w-[50%] lg:left-[25%] lg:right-[25%]">

            <div className="w-full bg-contain object-contain"
            style={{
                backgroundImage:`url(${image})`,    
                backgroundRepeat: "no-repeat",
            }}>
                <div className="top-0">
                    <h1 className="text-white">{title}</h1>
                    <p>Cooking Instructions</p>
                    <p className="text-white">{text}</p>
                    <a className="text-white" href={source} target="_blank">Original Source</a>
                    <button className="text-white" onClick={close_modal}>Close</button>                         
                </div>
            </div>
        </aside>
    )
}

export default Modal;
