import { useContext } from "react";
import { AppContext } from "../context";
import {FaRegThumbsUp} from 'react-icons/fa6';



const Meals= () => {

    const {loading, meals, render_meals}= useContext(AppContext);

    if(loading) {
        return (
            <section className="flex justify-center text-xl font-bold">
                <h2>Loading...</h2>
            </section>
        )
    }

    if(meals.length < 1) {
        return(
            <section className="flex justify-center text-xl font-bold">
                <h2>Unable to find dish that you want. Please try again.</h2>
            </section>
        )
    }
    return (
        <section className="border-b-2 border-black grid grid-cols-1 gap-5 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5
            pt-10 pb-[3.5rem] m-4">
            {meals.map((meal) => {
                const {idMeal, strMeal: title, strMealThumb: image}= meal;
                return (
                    render_meals(idMeal,title,image)
                )    
            })}
        </section>
    )
}

export default Meals;
