import { useContext } from "react";
import { AppContext } from "../context";
import {FaRegThumbsUp} from 'react-icons/FaReg';



const Meals= () => {

    const {meals}= useContext(AppContext);

    return (
        <section className="grid grid-cols-4 grid-gap-2 max-md: grid-cols-3">
            {meals.map((meal) => {
                const {idMeal, strMeal: title, strMealThumb: image}= meal;
                return (
                    <article key={idMeal}>
                        <img src={image} className="w-[10rem] max-w-[20rem] h-[10rem] max-h-[20rem]"/>
                        <footer>
                            <h5>{title}</h5>
                            <button className=""><FaRegThumbsUp/></button>
                        </footer>
                    </article>
                )    
            })}
        </section>
    )
}

export default Meals;
