import { useContext } from "react";
import { AppContext } from "../context";




function Modal() {
    const {selectedMeal, close_modal}= useContext(AppContext);

    const {strMealThumb: image, strMeal:title, strInstructions: text, strSource: source}= selectedMeal;

    return (
        <aside className="fixed bg-black w-full h-full z-[100] top-0 left-0 grid place-items-center opacity-80">
            <div className="relative w-[80vw] max-w-[800px] h-[80vh] rounded-lg">
                <div className="w-full h-full bg-white">
                    <img src={image} alt={title} className="w-full h-full object-cover opacity-50"/>
                </div>
                <div className="absolute top-0 left-0  w-full h-full flex flex-col items-center text-center p-10
                    overflow-y-scroll">
                    <h1 className="text-white text-4xl font-semibold mb-5">{title}</h1>
                    <p className="text-slate-300 text-xl mb-3">Cooking Instructions</p>
                    <p className="text-white text-lg">{text}</p>

                    <a className="text-white" href={source} target="_blank">Original Source</a>
                    <button className="text-white" onClick={close_modal}>Close</button>                   
                </div>
            </div>
        </aside>
    )
}

export default Modal;
