import { useContext, useState } from "react";
import { AppContext } from "../context";
import Meals from "./meals";




function Search() {

    const [text, setText]= useState('');

    const {setSearchedVal, fetch_rendom_meal}= useContext(AppContext);

    const handle_change= (event) => {
        setText(event.target.value);
    }

    const handle_submit= (event) => {
        event.preventDefault();
        if(text) {
            setSearchedVal(text);
        }
    }

    function handle_random_meal() {
        setSearchedVal('');
        setText('');
        fetch_rendom_meal();
    }

    return (
        <div className="flex justify-center">
            <form onSubmit={handle_submit} className="flex flex-col justify-center w-full
                min-[425px]:w-[18rem] md:w-[25rem] lg:w-[38rem] xl:w-[60%]">
                <div className="flex flex-wrap justify-center mx-4">
                    <input type="text" placeholder="Enter a name of dish..." value={text}
                        className="w-full h-[2rem] rounded-lg text-center placeholder:p-2"
                        onChange={handle_change}/>
                    <button type="submit" className="w-[5rem] h-[2rem] mt-2 rounded-lg min-[640px]:mt-auto hover:text-white active:bg-black"
                    >Search</button>
                </div>
                
                <div className="flex justify-center p-2">
                    <button type="btn" className="bg-black text-white w-[6.5rem] h-[2rem] rounded-lg hover:bg-white hover:text-black"
                        onClick={handle_random_meal} >Random Dish
                    </button>
                </div>
                
            </form>
        </div>
        
    )
}

export default Search;
