import { useContext } from "react";
import { AppContext } from "../context";




function Favourite() {
    const {favourite, select_meal,remove_from_favourites}= useContext(AppContext);
    return (
        <section className="bg-black m-4 p-2">
            <div>
                <h5 className="text-white">Favourites</h5>
                <div>
                    {favourite.map((item) => {
                        const {idMeal, strMealThumb: image}= item;

                        return (
                            <div key={idMeal}>
                                <img src={image} className="w-[5rem] h-[5rem]" onClick={() => select_meal(idMeal, true)}/>
                                <button onClick={() => remove_from_favourites(idMeal)}>
                                    Remove
                                </button>
                            </div>
                        )
                    })}
                </div>
            </div>
        </section>
    )
}

export default Favourite;
