import { useContext, useState } from "react";
import { AppContext } from "../context";




function Search() {

    const [text, setText]= useState('');

    const {setSearchedVal, fetch_rendom_meal}= useContext(AppContext);

    const handle_change= (event) => {
        setText(event.target.value);
    }

    const handle_submit= (event) => {
        event.preventDefault();
        if(text) {
            setSearchedVal(text);
        }
    }

    function handle_random_meal() {
        setSearchedVal('');
        setText('');
        fetch_rendom_meal();
    }
    return (
        <header>
            <h1>Food Crush</h1>
            <form onSubmit={handle_submit}>
                <input type="text" placeholder="Enter a name of dish" value={text} onChange={handle_change}/>
                <button type="submit">Search</button>
                <button type="btn" onClick={handle_random_meal}>Random Dish</button>
            </form>
        </header>
        
    )
}

export default Search;
